UpdateSourceArgs:
-lc "set -euo pipefail;
cd '{{$FullBaseDir}}';
mkdir -p assaultcube;
url='https://github.com/assaultcube/AC/releases/download/v1.3.0.2/AssaultCube_v1.3.0.2_linux.tar.gz';
tmp='assaultcube/ac.tar.gz';
echo 'Downloading AssaultCube...';
wget -qO \"$tmp\" \"$url\";
echo 'Extracting...';
( tar -xzf \"$tmp\" -C assaultcube --strip-components=1 2>/dev/null || tar -xzf \"$tmp\" -C assaultcube );
rm -f \"$tmp\";
chmod +x assaultcube/bin_unix/linux_64/ac_server || true;
mkdir -p assaultcube/config;
if [ ! -f assaultcube/config/server.conf ]; then
  cat > assaultcube/config/server.conf <<'EOF'
serverport {{$ServerPort}}
maxclients {{MaxClients}}
servername \"{{ServerName}}\"
EOF
fi
echo 'AssaultCube installed.'"
